/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  --accent:#38B6FF;
  --background-main:#01000B;
  --background-accent:#2D2BAC;
  --text-highlight:#E4A6A6;
}

/* Root element (use instead of `body`) */
#page {
  font-family: system-ui, sans-serif;
  color: #ffffff;
  line-height: 1.5;
  font-size: 1.125rem;

}

/* Elements */
.section-container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 2rem 2rem;
}

a.link {
  line-height: 1.3;

  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 2.5rem;
  line-height: 1.15;

}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 18px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.content {
  max-width: 900px;
  margin: 0 auto;
  padding: 3rem 2rem;

  p {
    margin-bottom: 1rem;
    line-height: 1.5;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
    line-height: 1.3;
    font-weight: 500;
    border-bottom: 2px solid var(--color-accent);
    transform: translateY(-2px); /* move link back into place */
    transition: var(--transition, 0.1s border);

    &:hover {
      border-color: transparent;
    }
  }

  h1 {
    font-size: 3rem;
    font-weight: 500;
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  h2 {
    font-size: 2.5rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h3 {
    font-size: 2rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    margin-top: 1.5rem;
    margin-bottom: 1.5rem;
    border-left: 5px solid var(--color-accent);
  }
}

.wrapper {
  display: flex;
  flex-direction: column;
  gap: 1.25rem;
}

.profile-card {
    display: flex;
    align-items: flex-start;
    gap: 2.25rem;
    padding-bottom:2.25rem;
}
.profile-card img {
    width: 5rem;
    height: 5rem;
    border-radius: 50%;
    object-fit: cover;
    flex-shrink: 0;
}

.profile-content h2 {
  font-size:1.5rem;
  font-weight:600;
  padding-bottom:1.25rem;

}
.profile-content {
      flex: 1;
      min-width: 0; 
  }

@media (max-width: 37.5rem) {
  .profile-card {
      flex-direction: column;
      text-align: center;
  }

  .profile-card img {
      align-self: center;
  }
}

.item {
  display: flex;
  align-items: flex-start;
  gap:2.2rem;
  margin-bottom: 40px;
}
.time{
  color:var(--text-highlight);
  font-weight:600;
}
.item img {
  width: 220px;
  border-radius: 5px;
  flex-shrink: 0;
}
.description{
   margin-bottom: 20px;
}
.timed{
  margin-left: 30px;
  font-weight:400;
}

@media (max-width: 768px) {
  .item {
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    margin-bottom: 30px;
  }

  .item img {
    width: 100%;
    max-width: 300px;
    margin-bottom: 15px;
  }

  .details {
    text-align: center;
  }

  .description {
    margin-bottom: 15px;
  }

  .timed {
    margin-left: 0;
  }
}

@media (max-width: 480px) {
  .item {
    gap: 1rem;
  }

  .time {
    font-size: 1rem;
  }

  .description {
    font-size: 0.9rem;
  }

  .timed {
    font-size: 0.9rem;
  }
}

.section-container {
  display: flex;
  justify-content: center;
  align-items: center;   
}

h1{
  font-size:1.5rem;
  font-weight:bold;
}

.footer{
  display:flex;
  flex-direction:column;
  gap:1rem;
  justify-content:center;
  align-items:center;
}


.image1 {
  flex: 1; 
  max-width: 250px;
  height: auto;
}

.image2 {
  flex: 2; 
  max-width: 500px;
  height: auto;
}

nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap:2rem;
}
nav .logo img {
  height: 150px;
  width: 200px;
}

nav ul {
  list-style: none;
  display: flex;
  margin: 0;
  padding: 0;
  gap: 25px;
}

nav ul li a {
  color: #fff;
  font-weight: 600;
  font-size: 1.2rem;
  transition: color 0.3s;
}
nav ul li a:hover {
  color: var(--accent); 
}

nav ul li a.active {
  color: var(--accent);
}
