    width: 100%;
    position: relative; /* remove sticky on small screens */
    top: auto;
  }

  .text-section {
    padding: 30px 20px;
  }

  .video-wrapper {
    width: 100%;
    max-width: 100%;
    border-radius: 0; /* optional: edge-to-edge on mobile */
  }
}

